plot gamma = Log(Sum((Max(high(period = aggregationPeriod1), close(period = aggregationPeriod1)[1]) - Min(low(period = aggregationPeriod1), close(period = aggregationPeriod1)[1])), nFE) / 
 (Highest(high(period = aggregationPeriod1), nFE) - Lowest(low(period = aggregationPeriod1), nFE)))
 / Log(nFE);
gamma.SetDefaultColor(Color.Yellow);
L0 = (1 gamma) * c + gamma * L0[1];
L1 = -gamma * L0 + L0[1] + gamma * L1[1];
L2 = -gamma * L1 + L1[1] + gamma * L2[1];
L3 = -gamma * L2 + L2[1] + gamma * L3[1];
if L0 >= L1
then {
 CU1 = L0 - L1;
 CD1 = 0;
} else {
 CD1 = L1 - L0;
 CU1 = 0;
}
if L1 >= L2
then {
 CU2 = CU1 + L1 - L2;
 CD2 = CD1;
} else {
 CD2 = CD1 + L2 - L1;
 CU2 = CU1;
}
if L2 >= L3
then {
 CU = CU2 + L2 - L3;
 CD = CD2;
} else {
 CU = CU2;
 CD = CD2 + L3 - L2;
}

RSI = if CU + CD <> 0 then CU / (CU + CD) else 0;
RSI.SetDefaultColor(Color.Cyan);
OS = if IsNaN(close(period = aggregationPeriod1)) then Double.NaN else 0.2;
OS.SetDefaultColor(Color.Gray);
OS.HideBubble();
OS.HideTitle();
OB = if IsNaN(close(period = aggregationPeriod1)) then Double.NaN else 0.8;
OB.SetDefaultColor(Color.Gray);
OB.HideBubble();
OB.HideTitle();
plot FEh = if isNaN(close(period = aggregationPeriod1)) then double.nan else .618;
FEh.SetStyle(Curve.Long_Dash);
FEh.HideBubble();
FEh.SetDefaultColor(Color.Dark_Gray);
FEh.HideTitle();
plot FEl = if isNaN(close(period = aggregationPeriod1)) then double.nan else .382;
FEl.SetStyle(Curve.Long_Dash);
FEl.SetDefaultColor(Color.Dark_Gray);
FEl.HideBubble();
FEl.HideTitle();
AddCloud(0, OS, Color.Green, Color.Green);
AddCloud(OB, 1, Color.Red, Color.Red);
Alert(RSI crosses below .9, "", Alert.BAR, Sound.Bell);
Alert(RSI crosses above .1, "", Alert.BAR, Sound.Bell);

