switch (current_period_trigger_zone) {
case "Zone 4":
 if (state == state.long and highagg_state == highagg_state.long) {
 if (bullZone4 and (highagg_bullZone2 or highagg_bullZone3 or highagg_bullZone4)) {
 action_confirmed = 4;
 } else {
 action_confirmed = 0;
 }
