# StudyName: blackFLAG_Futures_SwingArm_ATRTrail
# My preferred setting is 28 / 5 FOR ALL TIMEFRAMES
# Edited by: Jose Azcarate
# blackFLAG Futures Trading - FOR EDUCATIONAL PURPOSES ONLY
# TWITTER: @blackflagfuture

# SwingArm Watchlist by [USER=278]@fishstick1229[/USER]
# * Updated code below is to be used for custom watchlist column only **

#Updated text display by [USER=4199]@Fishbed[/USER]

# Combined Watchlist code to produce in chart label.

# 6/25/20 Updated by Scott K @sirrous
# Added overlapping swingarm timeframe notifications and alerts

# Set the aggperiod to the timeframe you want the zone to monitor for an overlap.  When the defined aggperiod's swingarm overlaps with the chart and price moves into that then overlap you will be notified.
# Labels only display for higher time periods (ie 15m aggperiod on a 10 min chart.
# Add the study multiple times and set them to different aggperoids to montior multiple times.


input high_aggperiod = AggregationPeriod.MIN;
input Enable_Zone_Overlap_Notifications = no;
input current_period_trigger_zone = {default "Zone 4", "Zone 3", "Zone 2"};
input trailType = {default modified, unmodified};
input ATRPeriod = 28;
input ATRFactor = 5;
input firstTrade = {default long, short};
input averageType = AverageType.WILDERS;

input fib1Level = 61.8;
input fib2Level = 78.6;
input fib3Level = 88.6;

Assert(ATRFactor > 0, "'atr factor' must be positive: " + ATRFactor);

## Current Chart Agg Period

def HiLo = Min(high - low, 1.5 * Average(high - low, ATRPeriod));
def HRef = if low <= high[1]
    then high - close[1]
    else (high - close[1]) - 0.5 * (low - high[1]);
def LRef = if high >= low[1]
    then close[1] - low
    else (close[1] - low) - 0.5 * (low[1] - high);

def trueRange;
switch (trailType) {
case modified:
    trueRange = Max(HiLo, Max(HRef, LRef));
case unmodified:
    trueRange = TrueRange(high, close, low);
}
def loss = ATRFactor * MovingAverage(averageType, trueRange, ATRPeriod);

def state = {default init, long, short};
def trail;
switch (state[1]) {
case init:
    if (!IsNaN(loss)) {
        switch (firstTrade) {
        case long:
            state = state.long;
            trail = close - loss;
        case short:
            state = state.short;
            trail = close + loss;
    }
    } else {
        state = state.init;
        trail = Double.NaN;
    }
case long:
    if (close > trail[1]) {
        state = state.long;
        trail = Max(trail[1], close - loss);
    } else {
        state = state.short;
        trail = close + loss;
    }
case short:
    if (close < trail[1]) {
        state = state.short;
        trail = Min(trail[1], close + loss);
    } else {
        state = state.long;
        trail = close - loss;
    }
}

def BuySignal = Crosses(state == state.long, 0, CrossingDirection.ABOVE);
def SellSignal = Crosses(state == state.short, 0, CrossingDirection.ABOVE);

def ex = if BuySignal then high else if SellSignal then low else if state == state.long then Max(ex[1], high) else if state == state.short then Min(ex[1], low) else ex[1];

def TrailingStop = trail;
def f1 = ex + (trail - ex) * fib1Level / 100;
def f2 = ex + (trail - ex) * fib2Level / 100;
def f3 = ex + (trail - ex) * fib3Level / 100;
def l100 = trail + 0;
def Fib1 = f1;
def Fib2 = f2;
def Fib3 = f3;

def bullAboveZone = state == state.long and close > Fib1;
def bullZone2 = state == state.long and close <= Fib1 and close > Fib2;
def bullZone3 = state == state.long and close <= Fib2 and close > Fib3;
def bullZone4 = state == state.long and close <= Fib3 and close > TrailingStop;

def bearZone2 = state == state.short and close >= Fib1 and close < Fib2;
def bearZone3 = state == state.short and close >= Fib2 and close < Fib3;
def bearZone4 = state == state.short and close >= Fib3 and close < TrailingStop;

## High Agg Period

def highagg_high = high(period = high_aggperiod);
def highagg_low = low(period = high_aggperiod);
def highagg_close = close(period = high_aggperiod);

def highagg_HiLo = Min(highagg_high - highagg_low, 1.5 * Average(highagg_high - highagg_low, ATRPeriod));
def highagg_HRef = if highagg_low <= highagg_high[1]
    then highagg_high - highagg_close[1]
    else (highagg_high - highagg_close[1]) - 0.5 * (highagg_low - highagg_high[1]);
def highagg_LRef = if highagg_high >= highagg_low[1]
    then highagg_close[1] - highagg_low
    else (highagg_close[1] - highagg_low) - 0.5 * (highagg_low[1] - highagg_high);

def highagg_trueRange;
switch (trailType) {
case modified:
    highagg_trueRange = Max(highagg_HiLo, Max(highagg_HRef, highagg_LRef));
case unmodified:
    highagg_trueRange = TrueRange(highagg_high, highagg_close, highagg_low);
}
def highagg_loss = ATRFactor * MovingAverage(averageType, highagg_trueRange, ATRPeriod);

def highagg_state = {default init, long, short};
def highagg_trail;
switch (highagg_state[1]) {
case init:
    if (!IsNaN(highagg_loss)) {
        switch (firstTrade) {
        case long:
            highagg_state = highagg_state.long;
            highagg_trail = highagg_close - highagg_loss;
        case short:
            highagg_state = highagg_state.short;
            highagg_trail = highagg_close + highagg_loss;
    }
    } else {
        highagg_state = highagg_state.init;
        highagg_trail = Double.NaN;
    }
case long:
    if (highagg_close > highagg_trail[1]) {
        highagg_state = highagg_state.long;
        highagg_trail = Max(highagg_trail[1], highagg_close - highagg_loss);
    } else {
        highagg_state = highagg_state.short;
        highagg_trail = highagg_close + highagg_loss;
    }
case short:
    if (highagg_close < highagg_trail[1]) {
        highagg_state = highagg_state.short;
        highagg_trail = Min(highagg_trail[1], highagg_close + highagg_loss);
    } else {
        highagg_state = highagg_state.long;
        highagg_trail = highagg_close - highagg_loss;
    }
}

def highagg_BuySignal = Crosses(highagg_state == highagg_state.long, 0, CrossingDirection.ABOVE);
def highagg_SellSignal = Crosses(highagg_state == highagg_state.short, 0, CrossingDirection.ABOVE);

def highagg_ex = if highagg_BuySignal then highagg_high else if highagg_SellSignal then highagg_low else if highagg_state == highagg_state.long then Max(highagg_ex[1], highagg_high) else if highagg_state == highagg_state.short then Min(highagg_ex[1], highagg_low) else highagg_ex[1];

def highagg_TrailingStop = highagg_trail;
def highagg_f1 = highagg_ex + (highagg_trail - highagg_ex) * fib1Level / 100;
def highagg_f2 = highagg_ex + (highagg_trail - highagg_ex) * fib2Level / 100;
def highagg_f3 = highagg_ex + (highagg_trail - highagg_ex) * fib3Level / 100;
def highagg_l100 = highagg_trail + 0;
def highagg_Fib1 = highagg_f1;
def highagg_Fib2 = highagg_f2;
def highagg_Fib3 = highagg_f3;

def highagg_bullAboveZone = highagg_state == highagg_state.long and highagg_close > highagg_Fib1;
def highagg_bullZone2 = highagg_state == highagg_state.long and highagg_close <= highagg_Fib1 and highagg_close > highagg_Fib2;
def highagg_bullZone3 = highagg_state == highagg_state.long and highagg_close <= highagg_Fib2 and highagg_close > highagg_Fib3;
def highagg_bullZone4 = highagg_state == highagg_state.long and highagg_close <= highagg_Fib3 and highagg_close > highagg_TrailingStop;

def highagg_bearZone2 = highagg_state == highagg_state.short and highagg_close >= highagg_Fib1 and highagg_close < highagg_Fib2;
def highagg_bearZone3 = highagg_state == highagg_state.short and highagg_close >= highagg_Fib2 and highagg_close < highagg_Fib3;
def highagg_bearZone4 = highagg_state == highagg_state.short and highagg_close >= highagg_Fib3 and highagg_close < highagg_TrailingStop;

AddLabel(yes, Concat(if high_aggperiod < 3600000 then high_aggperiod / 60000 + "m" else if high_aggperiod < 86400000 then high_aggperiod / 3600000 + "h" else if high_aggperiod < 604800000 then high_aggperiod / 86400000 + "D" else if high_aggperiod < 2592000000 then high_aggperiod / 604800000 + "Wk" else if high_aggperiod < 31536000000 then high_aggperiod / 2592000000 + "Mo" else high_aggperiod / 31536000000 + "Yr", if highagg_bullZone2 then ": ZONE 2"  else if highagg_bullZone3 then ": ZONE 3"  else if highagg_bullZone4 then ": ZONE 4" else if highagg_bearZone2 then ": ZONE 2"  else if highagg_bearZone3 then ": ZONE 3"  else if highagg_bearZone4 then ": ZONE 4" else ""), if highagg_state == highagg_state.long then Color.GREEN else Color.RED);

def action_confirmed;

switch (current_period_trigger_zone) {
case "Zone 4":
    if (state == state.long and highagg_state == highagg_state.long) {
        if (bullZone4 and (highagg_bullZone2 or highagg_bullZone3 or highagg_bullZone4)) {
            action_confirmed = 4;
        } else {
            action_confirmed = 0;
        }
    } else if (state == state.short and highagg_state == highagg_state.short) {
        if (bearZone4 and (highagg_bearZone2 or highagg_bearZone3 or highagg_bearZone4)) {
            action_confirmed = -4;
        } else {
            action_confirmed = 0;
        }

    } else {
        action_confirmed = 0;
    }
case "Zone 3":
    if (state == state.long and highagg_state == highagg_state.long) {
        if ((bullZone3 or bullZone4) and (highagg_bullZone2 or highagg_bullZone3 or highagg_bullZone4)) {
            action_confirmed = 3;
        } else {
            action_confirmed = 0;
        }
    } else if (state == state.short and highagg_state == highagg_state.short) {
        if ((bearZone3 or bearZone4) and (highagg_bearZone2 or highagg_bearZone3 or highagg_bearZone4)) {
            action_confirmed = -3;
        } else {
            action_confirmed = 0;
        }

    } else {
        action_confirmed = 0;
    }
case "Zone 2":
    if (state == state.long and highagg_state == highagg_state.long) {
        if ((bullZone2 or (bullZone3 or bullZone4)) and (highagg_bullZone2 or highagg_bullZone3 or highagg_bullZone4)) {
            action_confirmed = 2;
        } else {
            action_confirmed = 0;
        }
    } else if (state == state.short and highagg_state == highagg_state.short) {
        if ((bearZone2 or (bearZone3 or bearZone4)) and (highagg_bearZone2 or highagg_bearZone3 or highagg_bearZone4)) {
            action_confirmed = -2;
        } else {
            action_confirmed = 0;
        }

    } else {
        action_confirmed = 0;
    }
}

AddLabel((action_confirmed and Enable_Zone_Overlap_Notifications), if action_confirmed < -1 then "SELL CONDITION CONFIRMED - Price entered overlapping zones!" else if action_confirmed > 1 then "BUY CONDITION CONFIRMED - Price entered overlapping zones!" else "Waiting", if action_confirmed > 1 then Color.GREEN else if action_confirmed < -1 then Color.RED else Color.WHITE);
#Debug Label
#AddLabel((action_confirmed or always_show_label), action_confirmed, Color.White);

#add plot for scanner
plot sell_confirm = if (action_confirmed < -1) then high else Double.NaN;
sell_confirm.SetDefaultColor(Color.WHITE);
sell_confirm.SetPaintingStrategy(PaintingStrategy.BOOLEAN_ARROW_DOWN);
sell_confirm.SetLineWeight(1);
sell_confirm.Hide();

plot buy_confirm = if (action_confirmed > 1) then low else Double.NaN;
buy_confirm.SetDefaultColor(Color.WHITE);
buy_confirm.SetPaintingStrategy(PaintingStrategy.BOOLEAN_ARROW_UP);
buy_confirm.SetLineWeight(1);
buy_confirm.Hide();

