switch (state[1]) {
case init:
 if (!IsNaN(loss)) {
 switch (firstTrade) {
 case long:
 state = state.long;
 trail = close - loss;
 case short:
 state = state.short;
 trail = close + loss;
 }
 } else {
 state = state.init;
 trail = Double.NaN;
