input fastLength = 12;
input slowLength = 26;
input MACDLength = 9;
input displace = 0;
def fastAvg = Sum(volume * price, fastLength) / Sum(volume, fastLength);
def slowAvg = Sum(volume * price, slowLength) / Sum(volume, slowLength);
def Value = fastAvg - slowAvg;
def Avg = ExpAverage(Value, MACDLength);
def MACD_Diff = Value - Avg;
#def ZeroLine = 0;

input def_cond = no;
def MACD_cond_logic = if (def_cond) then MACD_Diff <= 0 else yes;


