def twoBarPivotMACD_Sell = MACD_Diff < MACD_Diff[1] and MACD_Diff[1] < MACD_Diff[2] and MACD_Diff[2] > MACD_Diff[3] and MACD_Diff[3] > MACD_Diff [4];


