def expthismonth = (if dow > 5 then 27 else 20) - dow;
def exp_opt = month + (dom > expthismonth);
switch (timePerProfile) {
case CHART:
 period = 0;
case MINUTE:
 period = Floor(seconds / 60 + day_number * 24 * 60);
case FIVE_MINUTE:
 period = Floor(seconds / 60 + day_number * 24 * 60);
case TEN_MINUTE:
 period = Floor(seconds / 60 + day_number * 24 * 60);
case FIFTEEN_MINUTE:
 period = Floor(seconds / 60 + day_number * 24 * 60);
case THIRTY_MINUTE:
 period = Floor(seconds / (30 * 60) + day_number * 24 * 60);
case HOUR:
 period = Floor(seconds / 3600 + day_number * 24);
case TWO_HOUR:
 period = Floor(seconds / (2 * 3600) + day_number * 24);
case FOUR_HOUR:
 period = Floor(seconds / (4 * 3600) + day_number * 24);
case DAY:
 period = CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1;
case TWO_DAY:
 period = 2 * (CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1);
case THREE_DAY:
 period = 3 * (CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1);
case FOUR_DAY:
 period = 4 * (CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1);
case WEEK:
 period = Floor(day_number / 7);
case TWO_WEEK:
 period = Floor(day_number / 14);
case THREE_WEEK:
 period = Floor(day_number / 21);
case MONTH:
 period = Floor(month - First(month));
case "OPT EXP":
 period = exp_opt - First(exp_opt);
case BAR:
 period = BarNumber() - 1;
}
