#
# Author: Kory Gill, [USER=212]@korygill[/USER]
#
# Created from idea on https://usethinkscript.com/threads/finite-volume-elements.593/
# Original idea is from http://mkatsanos.com/FVE.html
#
# VERSION HISTORY (sortable date and time (your local time is fine), and your initials
# 20190907-1200-KG - Created.

###############################################################################
### NORMALIZATION (Volume Damping adaptation) by Ramon Del Villar AKA PElonsax
### 7/20/2020 #################################################################
###############################################################################
# The damping logic is taken directly from VFI #

#declare once_per_bar;
declare lower;
input AGG = aggregationPeriod.FIVE_MIN;
input CutOffAG = 0.003;
input Samples_Used = 22;
def samplesAG = Samples_Used * (AGG/60000);


#
# inputs
#
input Samples = 22;

#
# logic
#
def bn = BarNumber();
def nan = double.NaN;


input maxVolumeCutOff = 2.5;
###Coefficient used in calculation of Cut Off value. Lower values mean stronger damping of volume spikes###

Assert(maxVolumeCutOff > 0, "'max volume cut off' must be positive: " + maxVolumeCutOff);

####
def timeFrame = getAggregationPeriod();
def testTimeFrames = if timeFrame < AGG then yes else no;

AddLabel(yes, if testTimeFrames then "Time Frames Are Correct" else "Time Frames Are Wrong", if testTimeFrames then color.GREEN else color.RED);



def cutOff = 0.2 * StDev(Log(hlc3(period = AGG)) - Log(hlc3(period = AGG)[1]), 30) * close(period = AGG);
def hlcChange = hlc3(period = AGG) - hlc3(period = AGG)[1];
def avgVolume = Average(volume(period = AGG), 30)[1];
def minVolume = Min(volume(period = AGG), avgVolume * maxVolumeCutOff);
def dirVolume = if hlcChange > cutOff
 then minVolume
 else if hlcChange < -cutOff
 then -minVolume
 else 0;


def mf = (close(period = AGG) - (high(period = AGG)+low(period = AGG))/2) + hlc3(period = AGG) - hlc3(period = AGG) [1];
def fveSum = Sum(dirVolume, samplesAG);

def fve = if bn > samplesAG
 then (fveSum / (Average(avgVolume, samplesAG) * samplesAG)) * 100
 else nan;


#
# plots
#
plot pFVE = fve;

plot pFVE_MA = MovingAverage(AverageType.SIMPLE, fve, 30);
pFVE_MA.SetDefaultColor(Color.Gray);
PFVE_MA.SetPaintingStrategy(PaintingStrategy.DASHES);

plot zeroLine = 0;
zeroLine.SetDefaultColor(Color.White);
pFVE.AssignValueColor(if pFVE > pFVE_MA then color.green else color.red) ;
pFVE.SetLineWeight(3);

plot pFVE_Bull = if pFVE crosses above pFVE_MA then pFVE else double.NaN ;
pFVE_Bull.SetPaintingStrategy(PaintingStrategy.SQUARES);
pFVE_Bull.SetLineWeight(5);
pFVE_Bull.SetDefaultColor(color.dark_green) ;

plot pFVE_Bear = if pFVE > pFVE_MA and pFVE[1] > pFVE then pFVE else double.NaN ;
pFVE_Bear.SetPaintingStrategy(PaintingStrategy.SQUARES);
pFVE_Bear.SetLineWeight(5);
pFVE_Bear.SetDefaultColor(color.white) ;

