plot FastAvg = mov_avg_Fast;
plot SlowAvg = mov_avg_Slow;
SuperfastAvg.setdefaultColor(color.yellow);
FastAvg.setdefaultColor(color.cyan);
SlowAvg.setdefaultColor(color.blue);

