FEh.SetDefaultColor(Color.GRAY);
FEh.HideTitle();
plot FEl = if IsNaN(c) then Double.NaN else .382;
FEl.SetStyle(Curve.SHORT_DASH);
FEl.SetDefaultColor(Color.GRAY);
FEl.HideBubble();
FEl.HideTitle();
