 switch (firstTrade) {
 case long:
 state = state.long;
 trail = close - loss;
 case short:
 state = state.short;
 trail = close + loss;
 }
 } else {
 state = state.init;
 trail = Double.NaN;
 }
case long:
 if (close > trail[1]) {
 state = state.long;
 trail = Max(trail[1], close - loss);
