input averageTypefast = AverageType.simple;
input averageTypeSlow = AverageType.EXPONENTIAL;



def priceSupperFast ;
def priceFast ;
def priceSlow ;
#Heikin Ashi
def haClose = (open+high+low+close)/4; #Heikin Ashi Close: The arithmetic mean of the current high, low, open, and close prices.
def haOpen = (haOpen[1] + haClose[1])/2 ;# Heikin Ashi Open: The midpoint of the previous candle.
def haHigh = max(high, max( haOpen, haClose) ) ;#Heikin Ashi High: The highest of the following: the actual high, the Heikin Ashi Open, or the Heikin Ashi Close.
def haLow = Min(low,min( haOpen, haClose));# Heikin Ashi Low: The lowest of the following: the actual low, the Heikin Ashi Open, or the Heikin Ashi Close.

if useHeikinAshi 

 then
 {
 priceSupperFast = (haHigh +haLow+ haclose)/3;
 priceFast = haclose;
 priceSlow = haclose;
 }
else
 {
 priceSupperFast = pSupperFast;
 priceFast = pFast;
 priceSlow = pSlow;
 }



