# Pivot values will be off the day following market holidays and possibly the day after rollover.
# On these days make "AutoPivots=No" and manually draw pivots. Change back to "AutoPivots=Yes" the day after.

input PivotTimeFrame = { "15 MIN" , "30 MIN", "1 HOUR", "2 HOURs", "4 HOURs", default "DAY", "2 DAYS", "3 DAYS", "4 DAYS", WEEK, MONTH, "OPT EXP", QUARTER, YEAR}; #AggregationPeriod.DAY;#
input ProfileTimeFrame = { CHART, FIVE_MINUTE, TEN_MINUTE, FIFTEEN_MINUTE, THIRTY_MINUTE, HOUR, TWO_HOUR, FOUR_HOUR, default DAY, TWO_DAY, THREE_DAY, FOUR_DAY, WEEK, TWO_WEEK, THREE_WEEK, MONTH, "OPT EXP", BAR};
input ShowCloud1 = yes;#Hint ShowCloud: Shows the value area as a cloud.
input ShowCloud2 = yes;#Hint ShowCloud: Shows the value area as a cloud.
input showOnlyToday = no;
input VaPrevious = yes;
input AutoPivots = yes;#Hint AutoPivots: Turns off pivot lines, value area stays on.
input showVAH = no;
input Labels = {default "Proximity", "Off", "All", "ValueAreaOnly"};#Hint Labels: Labels at top of chart.
input ShowPivotPoint = yes;#Hint ShowPivotPoint: Hides Pivot Point plot.
input ShowLevel4 = yes;#Hint ShowLevel4: Will display S4 and R4 for days with large moves.
input ShowLevel5 = yes;#Hint ShowLevel4: Will display S4 and R4 for days with large moves.
input ShowLevel6 = no;#Hint ShowLevel4: Will display S4 and R4 for days with large moves.
input ShowLevel7 = no;#Hint ShowLevel4: Will display S4 and R4 for days with large moves.
input ShowLevel8 = no;#Hint ShowLevel4: Will display S4 and R4 for days with large moves.
input PivotBubbles = yes;#yes;#Hint PivotBubbles: Shows bubbles on pivot plot lines.
input ValueBubbles = yes;#yes;#Hint ValueBubbles: Shows bubbles on value plot lines.
input PlotStartTime = 0930;#Hint PlotStartTime: Move to earlier time to shift bubbles to the left.


input CloudOpenOnly = yes;#Hint CloudOpenOnly: Will only cloud open candles.
input HideAfterHoursLabel = no;#Hint HideAfterHoursLabel: Hides "NoShadowPlotAfterHours" label.
input VpocBubbles = no;#yes;#Hint VpocBubbles: Shows bubbles on VPOC plot lines.

input ShowS1 = yes;#yes;#Hint ShowS1: Hide/Show S1.
input ShowS2 = yes;#Hint ShowS2: Hide/Show S2.
input ShowS3 = yes;#yes;#Hint ShowS3: Hide/Show S3.
input ShowR1 = yes;#yes;#Hint ShowR1: Hide/Show R1.
input ShowR2 = yes;#Hint ShowR2: Hide/Show R2.
input ShowR3 = yes;#yes;#Hint ShowR3: Hide/Show R3.
input ShowORB = no;#Hint ShowORB: Displays 2 small, dashed lines at the top/bottom of opening range.
input OrbTime = 30;#Hint OrbTime: Defines the time range in minutes for the ORB.

input triggerUpperVaLimitHigh = 5;
input triggerUpperVaLimitLow = -5;
input triggerLowerVaLimitHigh = 5;
input triggerLowerVaLimitLow = -5;
input showLines = no;
input opacity = 20;

def Na = Double.NaN;
input showDebugLable = yes;
