#AddLabel (yes,Chart1);
#Pivot Functions
def Res1 = (2 * PPoint) - PrevLow;
def Supp1 = (2 * PPoint) - PrevHigh;
def Res2 = PPoint + (Res1 - Supp1);
def Supp2 = PPoint - (Res1 - Supp1);
def Res3 = PrevHigh + 2 * (PPoint - PrevLow);
def Supp3 = PrevLow - 2 * (PrevHigh - PPoint);
def Res4 = PrevHigh + 3 * (PPoint - PrevLow);
def Supp4 = PrevLow - 3 * (PrevHigh - PPoint);


#def Res1 = (2 * PPoint) - PrevLow;
#def Supp1 = (2 * PPoint) - PrevHigh;
#def Res2 = PPoint + (Res1 - Supp1); # def Res2 = PPoint + (Res1 - Supp1);
#def Supp2 = PPoint - (Res1 - Supp1);
#def Res3 = PPoint + 2 * (PrevHigh - PrevLow);# def Res3 = PrevHigh + 2 * (PPoint - PrevLow);
#def Supp3 = PPoint - 2 * (PrevHigh - PrevLow);
#def Res4 = PPoint + 3 * (PrevHigh - PrevLow);#
#def Supp4 = PPoint - 3 * (PrevHigh - PrevLow);

def Res5 = PrevHigh + 4 * (PPoint - PrevLow);
def Supp5 = PrevLow - 4 * (PrevHigh - PPoint);

def Res6 = PrevHigh + 5 * (PPoint - PrevLow);
def Supp6 = PrevLow - 5 * (PrevHigh - PPoint);

def Res7 = PrevHigh + 6 * (PPoint - PrevLow);
def Supp7 = PrevLow - 6 * (PrevHigh - PPoint);

def Res8 = PrevHigh + 7 * (PPoint - PrevLow);
def Supp8 = PrevLow - 7 * (PrevHigh - PPoint);




#Rounding Functions
def round = 0.01;
def RI = RoundDown(Res1, 0) - ((Round(((RoundDown(Res1, 0) - Res1) / round), 0)) * round);
def SI = RoundDown(Supp1, 0) - ((Round(((RoundDown(Supp1, 0) - Supp1) / round), 0)) * round);
def RII = RoundDown(Res2, 0) - ((Round(((RoundDown(Res2, 0) - Res2) / round), 0)) * round);
def SII = RoundDown(Supp2, 0) - ((Round(((RoundDown(Supp2, 0) - Supp2) / round), 0)) * round);
def RIII = RoundDown(Res3, 0) - ((Round(((RoundDown(Res3, 0) - Res3) / round), 0)) * round);
def SIII = RoundDown(Supp3, 0) - ((Round(((RoundDown(Supp3, 0) - Supp3) / round), 0)) * round);
def RIV = RoundDown(Res4, 0) - ((Round(((RoundDown(Res4, 0) - Res4) / round), 0)) * round);
def SIV = RoundDown(Supp4, 0) - ((Round(((RoundDown(Supp4, 0) - Supp4) / round), 0)) * round);
def PivP = RoundDown(PPoint, 0) - ((Round(((RoundDown(PPoint, 0) - PPoint) / round), 0)) * round);

def RV = RoundDown(Res5, 0) - ((Round(((RoundDown(Res5, 0) - Res5) / round), 0)) * round);
def SV = RoundDown(Supp5, 0) - ((Round(((RoundDown(Supp5, 0) - Supp5) / round), 0)) * round);
def RVI = RoundDown(Res6, 0) - ((Round(((RoundDown(Res6, 0) - Res6) / round), 0)) * round);
def SVI = RoundDown(Supp6, 0) - ((Round(((RoundDown(Supp6, 0) - Supp6) / round), 0)) * round);
def RVII = RoundDown(Res7, 0) - ((Round(((RoundDown(Res7, 0) - Res7) / round), 0)) * round);
def SVII = RoundDown(Supp7, 0) - ((Round(((RoundDown(Supp7, 0) - Supp7) / round), 0)) * round);
def RVIII = RoundDown(Res8, 0) - ((Round(((RoundDown(Res8, 0) - Res8) / round), 0)) * round);
def SVIII = RoundDown(Supp8, 0) - ((Round(((RoundDown(Supp8, 0) - Supp8) / round), 0)) * round);




#Plots
plot VH = If(Chart1 and VAH > 0, VAH, Na);
plot PC = If(Chart1 and POC > 0, POC, Na);
plot VL = If(Chart1 and VAL > 0, VAL, Na);

##Auto Show 
def R1_auto_show;
def R2_auto_show;
def R3_auto_show;
def R4_auto_show;
def R5_auto_show;
def R6_auto_show;
def R7_auto_show;
def R8_auto_show;

def S1_auto_show;
def S2_auto_show;
def S3_auto_show;
def S4_auto_show;
def S5_auto_show;
def S6_auto_show;
def S7_auto_show;
def S8_auto_show;

def cmax = highestAll( count);
def R1AutoShow = (close>VH or (Between(RI,VL,VH) and close>VL));
def S1AutoShow = (close<VL or (Between(SI,VL,VH) and close<VH));;
def R2AutoShow = close > VH and close < RV;
def S2AutoShow = (close < VL) and close > SV ;
def R3AutoShow = close > (RI + ((RII - RI) / 2));;
def S3AutoShow = close < (SI - ((SI - SII) / 2));
def R4AutoShow = close > (RII + ((RIII - RII) / 2));
def S4AutoShow = close < (SII - ((SII - SIII) / 2));
def R5AutoShow = close > (RIII + ((RIV - RIII) / 2));
def S5AutoShow = close < (SIII - ((SIII - SIV) / 2));
def R6AutoShow = close > (RIV + ((RV - RIV) / 2));
def S6AutoShow = close < (SIV - ((SVI - SV) / 2));
def R7AutoShow = close > (RV + ((RVI - RV) / 2));
def S7AutoShow = close < (SV - ((SV - SVI) / 2));
def R8AutoShow = close > (RVI + ((RVII - RVI) / 2));
def S8AutoShow = close < (SVI - ((SVI - SVII) / 2));

R1_auto_show = if ( (period==period[1]) and R1_auto_show[1] ) then R1_auto_show[1] else R1AutoShow;
R2_auto_show = if ( (period==period[1]) and R2_auto_show[1] ) then R2_auto_show[1] else R2AutoShow;
R3_auto_show = if ( (period==period[1]) and R3_auto_show[1] ) then R3_auto_show[1] else R3AutoShow;
R4_auto_show = if ( (period==period[1]) and R4_auto_show[1] ) then R4_auto_show[1] else R4AutoShow;
R5_auto_show = if ( (period==period[1]) and R5_auto_show[1] ) then R5_auto_show[1] else R5AutoShow;
R6_auto_show = if ( (period==period[1]) and R6_auto_show[1] ) then R6_auto_show[1] else R6AutoShow;
R7_auto_show = if ( (period==period[1]) and R7_auto_show[1] ) then R7_auto_show[1] else R7AutoShow;
R8_auto_show = if ( (period==period[1]) and R8_auto_show[1] ) then R8_auto_show[1] else R8AutoShow;


S1_auto_show = CompoundValue(2,if ( (period==period[1]) and S1_auto_show[1] ) then S1_auto_show[1] else S1AutoShow,S1AutoShow);
S2_auto_show = if ( (period==period[1]) and S2_auto_show[1] ) then S2_auto_show[1] else S2AutoShow;
S3_auto_show = if ( (period==period[1]) and S3_auto_show[1] ) then S3_auto_show[1] else S3AutoShow;
S4_auto_show = if ( (period==period[1]) and S4_auto_show[1] ) then S4_auto_show[1] else S4AutoShow;
S5_auto_show = if ( (period==period[1]) and S5_auto_show[1] ) then S5_auto_show[1] else S5AutoShow;
S6_auto_show = if ( (period==period[1]) and S6_auto_show[1] ) then S6_auto_show[1] else S6AutoShow;
S7_auto_show = if ( (period==period[1]) and S7_auto_show[1] ) then S7_auto_show[1] else S7AutoShow;
S8_auto_show = if ( (period==period[1]) and S8_auto_show[1] ) then S8_auto_show[1] else S8AutoShow;

plot gg=(S1AutoShow);#==1);
plot ggg = ( s1_auto_show);# if ( isnan( s1_autoshow))then 1 else double.NaN ;
gg.SetHiding(0);
