# Pivot values will be off the day following market holidays and possibly the day after rollover.
# On these days make "AutoPivots=No" and manually draw pivots. Change back to "AutoPivots=Yes" the day after.

input AggPeriod = { "CHART", "5 MIN" , "10 MIN", "15 MIN" , "30 MIN", "1 HOUR", "2 HOURs", "4 HOURs", default "DAY", "2 DAYS", "3 DAYS", "4 DAYS", WEEK, MONTH, "OPT EXP", QUARTER, YEAR}; #AggregationPeriod.DAY;#
input VaPrior = yes;
input ShowCloud1 = no;#Hint ShowCloud: Shows the value area as a cloud.
input ShowCloud2 = yes;#Hint ShowCloud: Shows the value area as a cloud.
input CloudOpenOnly = yes;#Hint CloudOpenOnly: Will only cloud open candles.
input showOnlyToday = no;

input AutoPivots = yes;#Hint AutoPivots: Turns off pivot lines, value area stays on.
def showVAH = no; # for debuging
input Labels = {default "Proximity", "Off", "All", "ValueAreaOnly"};#Hint Labels: Labels at top of chart.
input ShowPivotPoint = no;#Hint ShowPivotPoint: Hides Pivot Point plot.

input PivotBubbles = no;#yes;#Hint PivotBubbles: Shows bubbles on pivot plot lines.
input ValueBubbles = no;#yes;#Hint ValueBubbles: Shows bubbles on value plot lines.
input PlotStartTime = 0930;#Hint PlotStartTime: Move to earlier time to shift bubbles to the left.



input HideAfterHoursLabel = no;#Hint HideAfterHoursLabel: Hides "NoShadowPlotAfterHours" label.
input VpocBubbles = no;#yes;#Hint VpocBubbles: Shows bubbles on VPOC plot lines.

input ShowL1 = yes;#Hint ShowS1: Hide/Show S1 /R1.
input ShowL2 = yes;#Hint ShowS2: Hide/Show S2 /R2.
input ShowL3 =yes;#Hint ShowS3: Hide/Show S3 /R3.
input ShowLevel4UP = no;#Hint ShowLevel4 and UP: Will display S4 and R4 for days with large moves.

input ShowORB = no;#Hint ShowORB: Displays 2 small, dashed lines at the top/bottom of opening range.
input OrbTime = 30;#Hint OrbTime: Defines the time range in minutes for the ORB.

input triggerUpperVaLimitHigh = 5;
input triggerUpperVaLimitLow = -5;
input triggerLowerVaLimitHigh = 5;
input triggerLowerVaLimitLow = -5;
input showLines = no;
input opacity = 20;

def Na = Double.NaN;
input showDebugLable = yes;
#declare hide_on_daily;


#Previous Day Functions
def Day = GetDayOfWeek(GetYYYYMMDD());
def CloseTime = SecondsTillTime(1545) >= 0;
def OpenTime = SecondsFromTime(0930) >= 0;
def RegHrs = CloseTime and OpenTime;

###
def PLow ;
def PHigh ;
def PrevLow ;
def PrevHigh;
def a = if "TimeFrame" == "DAY" then 1 else 0;

if a {
 PLow = CompoundValue(1, If(Day == Day[1] and RegHrs and low < PLow[1], low, If(SecondsFromTime(0930) <= 0 and RegHrs, low, PLow[1])), low);
 PHigh = CompoundValue(1, If(Day == Day[1] and RegHrs and high > PHigh[1], high, If(SecondsFromTime(0930) <= 0 and RegHrs, high, PHigh[1])), high);
 PrevLow = If(Day != Day[1], PLow[1], PrevLow[1]);
 PrevHigh = If(Day != Day[1], PHigh[1], PrevHigh[1]);

#AddLabel (yes,period);
}

else {
 PLow = low(period = AggPeriod);
 PHigh = high(period = AggPeriod);
 PrevLow = low(period = AggPeriod)[1];
 PrevHigh = high(period = AggPeriod)[1];

}
##################
#Prior Day Profile - counting formula by functionform

def period;
def yyyymmdd = GetYYYYMMDD();
def seconds = SecondsFromTime(0);
def month = GetYear() * 12 + GetMonth();
def day_number = DaysFromDate(First(yyyymmdd)) + GetDayOfWeek(First(yyyymmdd));
def dom = GetDayOfMonth(yyyymmdd);
def dow = GetDayOfWeek(yyyymmdd - dom + 1);
def expthismonth = (if dow > 5 then 27 else 20) - dow;
def exp_opt = month + (dom > expthismonth);

switch (AggPeriod) {
case CHART:
 period = 0;

case "5 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);
case "10 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);

case "15 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);

case "30 MIN":
 period = Floor(seconds / (30 * 60) + day_number * 24 * 60);
 # timeFrame = "30 MINUTES"; #"2 DAYS"
case "1 HOUR":
 period = Floor(seconds / 3600 + day_number * 24);
 # timeFrame = "1 HOUR"; #"2 DAYS"
case "2 HOURS":
 period = Floor(seconds / (2 * 3600) + day_number * 24);
 # timeFrame = "2 HOURS"; #"2 DAYS"
case "4 HOURS":
 period = Floor(seconds / (4 * 3600) + day_number * 24);

case DAY:
 period = CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1;
 # timeFrame = "1 DAY";
case "2 DAYS":
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 2);
 # timeFrame = "2 DAYS";
case "3 DAYS" :
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 3);

case "4 DAYS":
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 4);

case WEEK:
 period = Floor(day_number / 7);


case MONTH:
 period = Floor(month - First(month));

case "OPT EXP":
 period = exp_opt - First(exp_opt);

case "Quarter":

 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 90);


case "Year":

 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) /365);



#case BAR:
 # period = BarNumber() - 1;
}


def count;
if VaPrior
{

 count = CompoundValue(1, if !(period != period[1]) then count[1] + 1 else 1, 1);

}
else
{



 count = CompoundValue(1, if period != period[1] then count[1] else (count[1] + period - period[1]), 0);



}
def cond = count < count[1] + period - period[1];

def Pivot = hlc3(period = AggPeriod)[1];



def priorperiod = if IsNaN(count[1]) then 1 else Max(count, count[1]);
profile vol1 = VolumeProfile("startNewProfile" = period != period[1], "onExpansion" = no);

def vahprior = vol1.GetHighestValueArea();
def pocprior = vol1.GetPointOfControl();
def valprior = vol1.GetLowestValueArea();

def vahprev = GetValue(vahprior, priorperiod);
def pocprev = GetValue(pocprior, priorperiod);
def valprev = GetValue(valprior, priorperiod);

#if showProfile then
def showProfile = no;
def showPointOfControl = no;

