R6_auto_show = if ( (period==period[1]) and R6_auto_show[1] ) then R6_auto_show[1] else R6AutoShow;
R7_auto_show = if ( (period==period[1]) and R7_auto_show[1] ) then R7_auto_show[1] else R7AutoShow;
R8_auto_show = if ( (period==period[1]) and R8_auto_show[1] ) then R8_auto_show[1] else R8AutoShow;


