#vol.Show(if showProfile then GlobalColor("Profile") else Color.CURRENT, if showPointOfControl then GlobalColor("Point Of Control") else Color.CURRENT, if showValueArea then GlobalColor("Value Area") else Color.CURRENT, opacity);
#profile vol = VolumeProfile("startNewProfile" = cond, "onExpansion" = no);
#vol.Show(if showProfile then GlobalColor("Profile") else Color.CURRENT, if showPointOfControl then GlobalColor("Point Of Control") else Color.CURRENT, if showValueArea then GlobalColor("Value Area") else Color.CURRENT, opacity);
def VAH = vahprev;
def VAL = valprev;
def POC = pocprev;


