#Value Area Functions
def PPoint = If(Pivot > 0, Pivot, Na);
def VArea = Between(close, VAL, VAH);
def VAreaabove = close > VAH;
def VAreabelow = close < VAL;


###%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
#def oneVol = Roundup(( VAHigh - VALow) / VAHigh, 2);
def oneVol = RoundUp(( VAH - VAL) / 100, 2);
def upperValimitH = VAH + oneVol * (triggerUpperVaLimitHigh);
def upperValimitL = VAH + oneVol * (triggerUpperVaLimitLow);

def lowerValimitH = VAL + oneVol * (triggerLowerVaLimitHigh);
def lowerValimitL = VAL + oneVol * (triggerLowerVaLimitLow);
#triggerUpperVaLimitlow = if triggerUpperVaLimitlow>=0 then triggerUpperVaLimitlow =-5 else triggerUpperVaLimitlow= triggerUpperVaLimitlow;

