#Pivot Functions
def Res1 = (2 * PPoint) - PrevLow;
def Supp1 = (2 * PPoint) - PrevHigh;
def Res2 = PPoint + (Res1 - Supp1);
def Supp2 = PPoint - (Res1 - Supp1);
def Res3 = PrevHigh + 2 * (PPoint - PrevLow);
def Supp3 = PrevLow - 2 * (PrevHigh - PPoint);
def Res4 = PrevHigh + 3 * (PPoint - PrevLow);
def Supp4 = PrevLow - 3 * (PrevHigh - PPoint);


#def Res1 = (2 * PPoint) - PrevLow;
#def Supp1 = (2 * PPoint) - PrevHigh;
#def Res2 = PPoint + (Res1 - Supp1); # def Res2 = PPoint + (Res1 - Supp1);
#def Supp2 = PPoint - (Res1 - Supp1);
#def Res3 = PPoint + 2 * (PrevHigh - PrevLow);# def Res3 = PrevHigh + 2 * (PPoint - PrevLow);
#def Supp3 = PPoint - 2 * (PrevHigh - PrevLow);
#def Res4 = PPoint + 3 * (PrevHigh - PrevLow);#
#def Supp4 = PPoint - 3 * (PrevHigh - PrevLow);

def Res5 = PrevHigh + 4 * (PPoint - PrevLow);
def Supp5 = PrevLow - 4 * (PrevHigh - PPoint);

def Res6 = PrevHigh + 5 * (PPoint - PrevLow);
def Supp6 = PrevLow - 5 * (PrevHigh - PPoint);

def Res7 = PrevHigh + 6 * (PPoint - PrevLow);
def Supp7 = PrevLow - 6 * (PrevHigh - PPoint);

def Res8 = PrevHigh + 7 * (PPoint - PrevLow);
def Supp8 = PrevLow - 7 * (PrevHigh - PPoint);




#Rounding Functions
def round = 0.01;
def RI = RoundDown(Res1, 0) - ((Round(((RoundDown(Res1, 0) - Res1) / round), 0)) * round);
def SI = RoundDown(Supp1, 0) - ((Round(((RoundDown(Supp1, 0) - Supp1) / round), 0)) * round);
def RII = RoundDown(Res2, 0) - ((Round(((RoundDown(Res2, 0) - Res2) / round), 0)) * round);
def SII = RoundDown(Supp2, 0) - ((Round(((RoundDown(Supp2, 0) - Supp2) / round), 0)) * round);
def RIII = RoundDown(Res3, 0) - ((Round(((RoundDown(Res3, 0) - Res3) / round), 0)) * round);
def SIII = RoundDown(Supp3, 0) - ((Round(((RoundDown(Supp3, 0) - Supp3) / round), 0)) * round);
def RIV = RoundDown(Res4, 0) - ((Round(((RoundDown(Res4, 0) - Res4) / round), 0)) * round);
def SIV = RoundDown(Supp4, 0) - ((Round(((RoundDown(Supp4, 0) - Supp4) / round), 0)) * round);
def PivP = RoundDown(PPoint, 0) - ((Round(((RoundDown(PPoint, 0) - PPoint) / round), 0)) * round);

def RV = RoundDown(Res5, 0) - ((Round(((RoundDown(Res5, 0) - Res5) / round), 0)) * round);
def SV = RoundDown(Supp5, 0) - ((Round(((RoundDown(Supp5, 0) - Supp5) / round), 0)) * round);
def RVI = RoundDown(Res6, 0) - ((Round(((RoundDown(Res6, 0) - Res6) / round), 0)) * round);
def SVI = RoundDown(Supp6, 0) - ((Round(((RoundDown(Supp6, 0) - Supp6) / round), 0)) * round);
def RVII = RoundDown(Res7, 0) - ((Round(((RoundDown(Res7, 0) - Res7) / round), 0)) * round);
def SVII = RoundDown(Supp7, 0) - ((Round(((RoundDown(Supp7, 0) - Supp7) / round), 0)) * round);
def RVIII = RoundDown(Res8, 0) - ((Round(((RoundDown(Res8, 0) - Res8) / round), 0)) * round);
def SVIII = RoundDown(Supp8, 0) - ((Round(((RoundDown(Supp8, 0) - Supp8) / round), 0)) * round);




