def S2AutoShow = (close < VL) and close > SV ;
def R3AutoShow = close > (RI + ((RII - RI) / 2));;
def S3AutoShow = close < (SI - ((SI - SII) / 2));
def R4AutoShow = close > (RII + ((RIII - RII) / 2));
def S4AutoShow = close < (SII - ((SII - SIII) / 2));
def R5AutoShow = close > (RIII + ((RIV - RIII) / 2));
def S5AutoShow = close < (SIII - ((SIII - SIV) / 2));
