#Time Functions
def timeFrame = getAggregationPeriod();
def prdMax =highestAll(if(!IsNaN(close[-1]),period,Na)) ;
def cnt =if(period > prdMax-2 ,1,Na);
def Chart2 = if showOnlyToday then cnt else 1 ;;#if showOnlyToday then (IsNaN(period[-1] and (period == period[1]))) else 1 ;
#def Chart2 = if showOnlyToday then MarketOpen and NewDay else 1 ;

