#Pivot Functions
input useFib =no;

###################



input FibUseAggregate = NO;



input length = 3;
def displace = -1;

input PivotPrice = {default CLOSE, HLC3, VWAP, OHLC4, OPEN, HL2};
input PivotLength = 1;

def averageType = AverageType.SIMPLE;
def fib1 = 0.236; #fib 0.236
def fib2 = 0.5; #fib2 0.5
def fib3 = 0.764; #fib3 0.764
def fib4 = 1.0; # Extreems




#def mov_avg = MovingAverage(averageType, price[-displace], length);

def ll;
def hh;
def pv;

if FibUseAggregate
{
 ll = low (period = AggPeriod)[-displace];;
 hh = high (period = AggPeriod)[-displace];
 switch (PivotPrice)
 {
 case "CLOSE":
 pv = close(period = AggPeriod) [-displace];
 case "HLC3" :
 pv = hlc3(period = AggPeriod)[-displace];

 case "VWAP" :
 pv = vwap(period = AggPeriod)[-displace];
 case "HL2" :
 pv = hl2(period = AggPeriod)[-displace];
 case "OHLC4" :
 pv = ohlc4(period = AggPeriod)[-displace];
 case "OPEN" :
 pv = open(period = AggPeriod)[-displace];
 }
}
else
{
 ll = average( low[-displace], length);#MovingAverage(averageType, low[-displace], length);
 hh = average( high[-displace], length);#MovingAverage(averageType, high[-displace], length);
 switch (PivotPrice)
 {
 case "CLOSE":
 pv = MovingAverage(averageType, close [-displace], PivotLength);;
 case "HLC3" :
 pv = MovingAverage(averageType,hlc3[-displace], PivotLength);;
 case "VWAP" :
 pv = MovingAverage(averageType,vwap[-displace], PivotLength);;
 case "HL2" :
 pv =MovingAverage(averageType, hl2[-displace], PivotLength);;
 case "OHLC4" :
 pv = MovingAverage(averageType,ohlc4[-displace], PivotLength);;
 case "OPEN" :
 pv = MovingAverage(averageType,open[-displace], PivotLength);;

 }
}




#
# logic
#
 def nan = Double.NaN;


 def delta = hh - ll;
 def pp_ = pv; #pivot Point
 def s1_ = pp_ - (fib1 * delta);
 def s2_ = pp_ - (fib2 * delta);
 def s3_ = pp_ - (fib3 * delta);
 def s4_ = pp_ - (fib4 * delta);
 def r1_ = pp_ + (fib1 * delta);
 def r2_ = pp_ + (fib2 * delta);
 def r3_ = pp_ + (fib3 * delta);
 def r4_ = pp_ + (fib4 * delta);





##########

def Res1 =if(usefib, r1_, (2 * PPoint) - PrevLow);
def Supp1 =if(usefib, s1_, (2 * PPoint) - PrevHigh);
def Res2 = if(usefib, r2_, PPoint + (Res1 - Supp1));
def Supp2 = if(usefib, s2_, PPoint - (Res1 - Supp1));
def Res3 = if(usefib, r3_, PrevHigh + 2 * (PPoint - PrevLow));
def Supp3 = if(usefib, s3_, PrevLow - 2 * (PrevHigh - PPoint));
def Res4 = if(usefib, r4_, PrevHigh + 3 * (PPoint - PrevLow));
def Supp4 = if(usefib, s4_, PrevLow - 3 * (PrevHigh - PPoint));
def Res5 = PrevHigh + 4 * (PPoint - PrevLow);
def Supp5 = PrevLow - 4 * (PrevHigh - PPoint);

def Res6 = PrevHigh + 5 * (PPoint - PrevLow);
def Supp6 = PrevLow - 5 * (PrevHigh - PPoint);

def Res7 = PrevHigh + 6 * (PPoint - PrevLow);
def Supp7 = PrevLow - 6 * (PrevHigh - PPoint);

def Res8 = PrevHigh + 7 * (PPoint - PrevLow);
def Supp8 = PrevLow - 7 * (PrevHigh - PPoint);











#Rounding Functions
def round = 0.01;
def RI = RoundDown(Res1, 0) - ((Round(((RoundDown(Res1, 0) - Res1) / round), 0)) * round);
def SI = RoundDown(Supp1, 0) - ((Round(((RoundDown(Supp1, 0) - Supp1) / round), 0)) * round);
def RII = RoundDown(Res2, 0) - ((Round(((RoundDown(Res2, 0) - Res2) / round), 0)) * round);
def SII = RoundDown(Supp2, 0) - ((Round(((RoundDown(Supp2, 0) - Supp2) / round), 0)) * round);
def RIII = RoundDown(Res3, 0) - ((Round(((RoundDown(Res3, 0) - Res3) / round), 0)) * round);
def SIII = RoundDown(Supp3, 0) - ((Round(((RoundDown(Supp3, 0) - Supp3) / round), 0)) * round);
def RIV = RoundDown(Res4, 0) - ((Round(((RoundDown(Res4, 0) - Res4) / round), 0)) * round);
def SIV = RoundDown(Supp4, 0) - ((Round(((RoundDown(Supp4, 0) - Supp4) / round), 0)) * round);


def RV = RoundDown(Res5, 0) - ((Round(((RoundDown(Res5, 0) - Res5) / round), 0)) * round);
def SV = RoundDown(Supp5, 0) - ((Round(((RoundDown(Supp5, 0) - Supp5) / round), 0)) * round);
def RVI = RoundDown(Res6, 0) - ((Round(((RoundDown(Res6, 0) - Res6) / round), 0)) * round);
def SVI = RoundDown(Supp6, 0) - ((Round(((RoundDown(Supp6, 0) - Supp6) / round), 0)) * round);
def RVII = RoundDown(Res7, 0) - ((Round(((RoundDown(Res7, 0) - Res7) / round), 0)) * round);
def SVII = RoundDown(Supp7, 0) - ((Round(((RoundDown(Supp7, 0) - Supp7) / round), 0)) * round);
def RVIII = RoundDown(Res8, 0) - ((Round(((RoundDown(Res8, 0) - Res8) / round), 0)) * round);
def SVIII = RoundDown(Supp8, 0) - ((Round(((RoundDown(Supp8, 0) - Supp8) / round), 0)) * round);
def PivP = RoundDown(PPoint, 0) - ((Round(((RoundDown(PPoint, 0) - PPoint) / round), 0)) * round);



#Plots
