def OpenTime = SecondsFromTime(0930) >= 0;
def RegHrs = CloseTime and OpenTime;

###
def PLow ;
def PHigh ;
def PrevLow ;
def PrevHigh;
def a = if "TimeFrame" == "DAY" then 1 else 0;

if a {
 PLow = CompoundValue(1, If(Day == Day[1] and RegHrs and low < PLow[1], low, If(SecondsFromTime(0930) <= 0 and RegHrs, low, PLow[1])), low);
 PHigh = CompoundValue(1, If(Day == Day[1] and RegHrs and high > PHigh[1], high, If(SecondsFromTime(0930) <= 0 and RegHrs, high, PHigh[1])), high);
 PrevLow = If(Day != Day[1], PLow[1], PrevLow[1]);
 PrevHigh = If(Day != Day[1], PHigh[1], PrevHigh[1]);

#AddLabel (yes,period);
}

else {
 PLow = low(period = AggPeriod);
 PHigh = high(period = AggPeriod);
 PrevLow = low(period = AggPeriod)[1];
 PrevHigh = high(period = AggPeriod)[1];

}
