#Prior Day Profile - counting formula by functionform

def period;
def yyyymmdd = GetYYYYMMDD();
def seconds = SecondsFromTime(0);
def month = GetYear() * 12 + GetMonth();
def day_number = DaysFromDate(First(yyyymmdd)) + GetDayOfWeek(First(yyyymmdd));
def dom = GetDayOfMonth(yyyymmdd);
def dow = GetDayOfWeek(yyyymmdd - dom + 1);
def expthismonth = (if dow > 5 then 27 else 20) - dow;
def exp_opt = month + (dom > expthismonth);

switch (AggPeriod) {
case CHART:
 period = 0;

case "5 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);
case "10 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);

case "15 MIN":
 period = Floor(seconds / 60 + day_number * 24 * 60);

case "30 MIN":
 period = Floor(seconds / (30 * 60) + day_number * 24 * 60);
 # timeFrame = "30 MINUTES"; #"2 DAYS"
case "1 HOUR":
 period = Floor(seconds / 3600 + day_number * 24);
 # timeFrame = "1 HOUR"; #"2 DAYS"
case "2 HOURS":
 period = Floor(seconds / (2 * 3600) + day_number * 24);
 # timeFrame = "2 HOURS"; #"2 DAYS"
case "4 HOURS":
 period = Floor(seconds / (4 * 3600) + day_number * 24);

case DAY:
 period = CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1;
 # timeFrame = "1 DAY";
case "2 DAYS":
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 2);
 # timeFrame = "2 DAYS";
case "3 DAYS" :
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 3);

case "4 DAYS":
 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 4);

case WEEK:
 period = Floor(day_number / 7);


case MONTH:
 period = Floor(month - First(month));

case "OPT EXP":
 period = exp_opt - First(exp_opt);

case "Quarter":

 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) / 90);


case "Year":

 period = Floor((CountTradingDays(Min(First(yyyymmdd), yyyymmdd), yyyymmdd) - 1) /365);



#case BAR:
 # period = BarNumber() - 1;
}


def count;
if VaPrior
{

 count = CompoundValue(1, if !(period != period[1]) then count[1] + 1 else 1, 1);

}
else
{



 count = CompoundValue(1, if period != period[1] then count[1] else (count[1] + period - period[1]), 0);



}
def cond = count < count[1] + period - period[1];

