#
# inputs
#
input RSI_prev = 2; #previous bar to check
input def_cond = no;
input Samples = 14;



#vwap vvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvvv

#input numDevUp = 2.0;
input vwapTimeFrame = {default DAY, WEEK, MONTH};

def cap = getAggregationPeriod();
def errorInAggregation =
    vwapTimeFrame == vwapTimeFrame.DAY and cap >= AggregationPeriod.WEEK or
    vwapTimeFrame== vwapTimeFrame.WEEK and cap >= AggregationPeriod.MONTH;
assert(!errorInAggregation, "timeFrame should be not less than current chart aggregation period");

def yyyyMmDd = getYyyyMmDd();
def periodIndx;
switch (vwapTimeFrame) {
case DAY:
    periodIndx = yyyyMmDd;
case WEEK:
    periodIndx = Floor((daysFromDate(first(yyyyMmDd)) + getDayOfWeek(first(yyyyMmDd))) / 7);
case MONTH:
    periodIndx = roundDown(yyyyMmDd / 100, 0);
}
def isPeriodRolled = compoundValue(1, periodIndx != periodIndx[1], yes);

def volumeSum;
def volumeVwapSum;
def volumeVwap2Sum;

if (isPeriodRolled) {
    volumeSum = volume;
    volumeVwapSum = volume * vwap;
    volumeVwap2Sum = volume * Sqr(vwap);
} else {
    volumeSum = compoundValue(1, volumeSum[1] + volume, volume);
    volumeVwapSum = compoundValue(1, volumeVwapSum[1] + volume * vwap, volume * vwap);
    volumeVwap2Sum = compoundValue(1, volumeVwap2Sum[1] + volume * Sqr(vwap), volume * Sqr(vwap));
}
def vWapprice = volumeVwapSum / volumeSum;
def deviation = Sqrt(Max(volumeVwap2Sum / volumeSum - Sqr(vWapprice), 0));

def VWAP = vWapprice;



input vwapCondition =no;
#input vp=close;
def conVwapBuy =if  vwapCondition then  close >= VWAp else 1;

#####^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^



#
# FVE logic
#
def bn = BarNumber();
def nan = double.NaN;


input maxVolumeCutOff = 2.5;
###Coefficient used in calculation of Cut Off value. Lower values mean stronger damping of volume spikes###

Assert(maxVolumeCutOff > 0, "'max volume cut off' must be positive: " + maxVolumeCutOff);

def cutOff = 0.2 * StDev(Log(hlc3) - Log(hlc3[1]), 30) * close;
def hlcChange = hlc3 - hlc3[1];
def avgVolume = Average(volume, 30)[1];
def minVolume = Min(volume, avgVolume * maxVolumeCutOff);
def dirVolume = if hlcChange > cutOff
    then minVolume
    else if hlcChange < -cutOff
        then -minVolume
        else 0;


def mf = (close - (high+low)/2) + hlc3 - hlc3 [1];
def fveSum = Sum(dirVolume, Samples);

def fve = if bn > Samples
          then (fveSum / (Average(avgVolume, Samples) * Samples)) * 100
          else nan;


#
# plots
#
def pFVE = fve;

def pFVE_MA = MovingAverage(AverageType.SIMPLE, fve, 30);
#pFVE_MA.SetDefaultColor(Color.Gray);
#PFVE_MA.SetPaintingStrategy(PaintingStrategy.DASHES);

