#pFVE_Bear.SetDefaultColor(color.white) ;

#AddCloud(pFVE, pFVE_MA, color.light_green, color.pink);



#####################################################################################################################################################



# Simple 2 Period RSI Trading Indicator
# Assembled by BenTen at useThinkScript.com
# You are free to use this code for personal use, and make derivative works from it. You are NOT GRANTED permission to use this code (or derivative works) for commercial purposes which includes and is not limited to selling, reselling, or packaging with other commercial indicators. Headers and attribution in this code should remain as provided, and any derivative works should extend the existing headers.

#MACD
input price = close;
#input length_macd = 14;
input displace = 0;
#input showBreakoutSignals = no;
input crossingType = {default above, below};

input fastLength = 12;
input slowLength = 26;
input MACDLength = 9;
def fastAvg = Sum(volume * price, fastLength) / Sum(volume, fastLength);
def slowAvg = Sum(volume * price, slowLength) / Sum(volume, slowLength);
def Value = fastAvg - slowAvg;
def Avg = ExpAverage(Value, MACDLength);
def MACD_Diff  = Value - Avg;
#def ZeroLine = 0;

# RSI
input rsiLength = 14;
input overBought = 80;
input overSold = 28;
input rsiBuyLevel =40;
input averageType = AverageType.WILDERS;
def NetChgAvg = MovingAverage(averageType, price - price[1], RSIlength);
def TotChgAvg = MovingAverage(averageType, AbsValue(price - price[1]), RSIlength);
def ChgRatio = if TotChgAvg != 0 then NetChgAvg / TotChgAvg else 0;
def RSI = 50 * (ChgRatio + 1);
#def OverSold = overSold;
#def OverBought = overBought;

#test
#plot p= VWAp;#(period= AggregationPeriod.DAY);

####################################

def cond_logic = if (def_cond) then MACD_Diff <= 0 else yes;
######
#def sig_buy = Crosses(RSI(length = RSIlength, averageType = averageType).RSI, over_Sold, crossingType == crossingType.above) within RSI_prev bars and pFVE_Bull and cond_logic; # and Diff <= 0;
